mapper.select:
 select 
 
 from goods_goods
 
	 
     1 = 1
   
  
     and seller_id = #{sellerId,jdbcType=INTEGER}
   
  
     and goods_code = #{goodsCode,jdbcType=VARCHAR}
   
   
     and name = #{name,jdbcType=VARCHAR}
   
   
     and category_id = #{categoryId,jdbcType=INTEGER}
   
   
     and thumb = #{thumb,jdbcType=VARCHAR}
   
   
     and picture1 = #{picture1,jdbcType=VARCHAR}
   
   
     and picture2 = #{picture2,jdbcType=VARCHAR}
   
   
     and picture3 = #{picture3,jdbcType=VARCHAR}
   
   
     and price = #{price,jdbcType=INTEGER}
   
   
     and vip_price = #{vipPrice,jdbcType=INTEGER}
   
   
     and special_selling_price = #{specialSellingPrice,jdbcType=INTEGER}
   
   
     and stock = #{stock,jdbcType=INTEGER}
   
   
     and isDefault = #{isdefault,jdbcType=INTEGER}
   
   
     and status = #{status,jdbcType=INTEGER}
   
   
     and create_time = #{createTime,jdbcType=TIMESTAMP}
   
   
     and pull_on_time = #{pullOnTime,jdbcType=TIMESTAMP}
   
   
     and new_pull_on_time = #{newPullOnTime,jdbcType=TIMESTAMP}
   
   
     and start_time = #{startTime,jdbcType=TIMESTAMP}
   
   
     and end_time = #{endTime,jdbcType=TIMESTAMP}
   
   
     and is_new_pull_on = #{isNewPullOn,jdbcType=INTEGER}
   
   
     and is_recommended_by_store_manager = #{isRecommendedByStoreManager,jdbcType=INTEGER}
   
   
     and is_special_selling = #{isSpecialSelling,jdbcType=INTEGER}
   
   
     and details = #{details,jdbcType=LONGVARCHAR}
