mapper.select:
 select 
 
 from order_store_order
 
	  
			1 = 1
   
   
     and order_code = #{orderCode,jdbcType=VARCHAR}
   
   
     and pay_order_code = #{payOrderCode,jdbcType=VARCHAR}
   
   
     and store_id = #{storeId,jdbcType=INTEGER}
   
   
     and user_id = #{userId,jdbcType=INTEGER}
   
   
     and total_price = #{totalPrice,jdbcType=INTEGER}
   
   
     and status = #{status,jdbcType=INTEGER}
   
   
     and create_time = #{createTime,jdbcType=TIMESTAMP}
   
   
     and payment_time = #{paymentTime,jdbcType=TIMESTAMP}
   
   
     and cancal_time = #{cancalTime,jdbcType=TIMESTAMP}

 select o.id,o.order_code,o.pay_order_code,o.store_id,o.distribution_type,o.user_id,o.coupon_id,o.total_price,o.address_id,o.express_price,o.coupon_price,o.refund_price,o.result_price,o.status,o.create_time,o.user_remark
 ,a.receiving_name,a.receiving_phone
 from order_store_order o,user_address a
 
	  
			o.address_id = a.id
   
   
     and order_code = #{orderCode,jdbcType=VARCHAR}
   
   
     and pay_order_code = #{payOrderCode,jdbcType=VARCHAR}
   
   
     and store_id = #{storeId,jdbcType=INTEGER}
   
   
     and user_id = #{userId,jdbcType=INTEGER}
   
   
     and total_price = #{totalPrice,jdbcType=INTEGER}
   
   
     and status = #{status,jdbcType=INTEGER}
   
   
     and create_time = #{createTime,jdbcType=TIMESTAMP}
   
   
     and payment_time = #{paymentTime,jdbcType=TIMESTAMP}
   
   
     and cancal_time = #{cancalTime,jdbcType=TIMESTAMP}
   
 
 order by o.id desc

select s.id as storeOrderId,s.total_price as totalPrice,s.store_id as storeId,s.status as status, e.name as storeName,e.avatar as storeAvatar, g.id as goodsOrderId,g.price as price,g.num as num, o.name as goodsName,o.thumb as thumb, u.attr_details as skuDetails from order_store_order s, order_goods_order g, goods_goods o, goods_sku u, seller_store e where s.order_code = g.store_order_code and g.goods_id = o.id and g.sku_id = u.id ands.store_id = e.id and s.userId = #{userId,jdbcType=INTEGER}
and s.status = #{status,jdbcType=INTEGER}
order by s.create_time desc

