mapper.insert:
insert into order_store_order (id, order_code, pay_order_code, 
  seller_id, store_id, user_id, 
  total_price, status, creat_time, 
  payment_time, cancal_time, user_remark
  )
values (#{id,jdbcType=INTEGER}, #{orderCode,jdbcType=VARCHAR}, #{payOrderCode,jdbcType=VARCHAR}, 
  #{sellerId,jdbcType=INTEGER}, #{storeId,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, 
  #{totalPrice,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, #{creatTime,jdbcType=TIMESTAMP}, 
  #{paymentTime,jdbcType=TIMESTAMP}, #{cancalTime,jdbcType=TIMESTAMP}, #{userRemark,jdbcType=VARCHAR}
  )

mapper.select:
select 

from order_store_order

  
    and order_code = #{orderCode,jdbcType=VARCHAR}
  
  
    and pay_order_code = #{payOrderCode,jdbcType=VARCHAR}
  
  
    and seller_id = #{sellerId,jdbcType=INTEGER}
  
  
    and store_id = #{storeId,jdbcType=INTEGER}
  
  
    and user_id = #{userId,jdbcType=INTEGER}
  
  
    and total_price = #{totalPrice,jdbcType=INTEGER}
  
  
    and status = #{status,jdbcType=INTEGER}
  
  
    and creat_time = #{creatTime,jdbcType=TIMESTAMP}
  
  
    and payment_time = #{paymentTime,jdbcType=TIMESTAMP}
  
  
    and cancal_time = #{cancalTime,jdbcType=TIMESTAMP}

select s.id as storeOrderId,s.total_price as totalPrice,s.store_id as storeId,s.status as status, e.name as storeName,e.avatar as storeAvatar, g.id as goodsOrderId,g.price as price,g.num as num, o.name as goodsName,o.thumb as thumb, u.attr_details as skuDetails from order_store_order s, order_goods_order g, goods_goods o, goods_sku u, seller_store e where s.order_code = g.store_order_code and g.goods_id = o.id and g.sku_id = u.id ands.store_id = e.id and s.userId = userId
and s.status = #{status,jdbcType=INTEGER}
order by s.create_time desc
